package ipfs

import (
	"fmt"

	"github.com/zde37/pinata-go-sdk/pinata"
)

func UploadToIPFS(filename string) (string, error) {

	// Expose the jwt cuz why not :)
	auth := pinata.NewAuthWithJWT("eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..kFVAcu0HqdECdgo_NV8jhPzPwlOnS0bvDL8WM1BL8zY")
	client := pinata.New(auth)

	response, err := client.PinFile(filename, nil)
	if err != nil {
		fmt.Println("Error:", err)
		return "" , err
	}

	message := fmt.Sprintf("File pinned successfully. Link: https://ipfs.io/ipfs/%s\n", response.IpfsHash)
	return message, nil
}